const maxC2H4 = 82;
const minC2H4 = 55;

const amplitude = (maxC2H4 - minC2H4) / 2;
const offset = (maxC2H4 + minC2H4) / 2;
const period = 60 * 1000;
const noiseMean = 0;
const noiseStdDev = 0.4;

let timestamp = msg.payload;
let elapsedTime = timestamp % period;
let phase = (2 * Math.PI * elapsedTime) / period;

let sinValue = amplitude * Math.sin(phase) + offset;

let randomNoise = noiseMean + (noiseStdDev * Math.random());
sinValue += randomNoise;

msg.topic = 'c2h4'
msg.payload = sinValue;

return msg;
